function highlightSearchTerm(text, searchTerm) {
    const re = new RegExp(`(${searchTerm})`, 'gi'); 
    return text.replace(re, '<span class="highlight">$1</span>');
}

document.addEventListener('DOMContentLoaded', function () {
    const params = new URLSearchParams(window.location.search);
    const query = params.get('query').trim().toLowerCase();

    const searchContent = () => {
        return contentIndex.filter(item =>
            item.title.toLowerCase().includes(query) ||
            item.description.toLowerCase().includes(query)
        );
    };

    const displayResults = (results) => {
        const resultsContainer = document.getElementById('search-results');
        resultsContainer.innerHTML = '';

        if (results.length === 0) {
            resultsContainer.innerHTML = '<p>Няма намерени резултати.</p>';
            return;
        }

        results.forEach(result => {
            const resultElement = document.createElement('div');
            resultElement.className = 'result-item';
            const highlightedTitle = highlightSearchTerm(result.title, query);
            const highlightedDescription = highlightSearchTerm(result.description, query);
            resultElement.innerHTML = `<h3>${highlightedTitle}</h3><p>${highlightedDescription}</p><button onclick="window.location.href='${result.url}'">Прочети повече</button>`;
            resultsContainer.appendChild(resultElement);
        });
    };

    displayResults(searchContent());
});

